transPherToAnt :: GraphATuple -> GraphPTuple -> GraphATuple
transPherToAnt graphAT graphPT = do
        let aphers = listOfNodes $ brokenUpGraph graphAT
        let pphers = listOfNodes $ brokenUpGraph graphPT
        let mpphers = map Just pphers
        let mppNphers = map (\x -> do {if x == Just 0.0 then Nothing else x}) mpphers -- adding Nothings where Just 0.0
        let newAnts = zipWith pherToAnt aphers pphers -- ants now have smell
