-- | Takes an Ant an amount of Pheremone and returns an Ant with pheremone increased by a proportion of the pheremone that was passed to the function.
pherToAnt :: Maybe Ant -> Double -> Maybe Ant
pherToAnt ant pher = do 
        if isNothing ant
                then Nothing
                else pherToAnt' (fromJust ant) pher

pherToAnt' ant pher = do        
        let currLevel = pherLevel ant
        let additPher = pher*0.25 -- additional pheremone to add (1/4 of the pher at location)
        let newAnt = Just(Ant (antId ant) (antDir ant) ((pherLevel ant) + additPher)  (age ant) (aim ant))
        newAnt
        

-- | TODO
transAntToPher :: GraphPTuple -> GraphATuple -> GraphPTuple
transAntToPher graphPT graphAT = do
        let aphers = listOfNodes $ brokenUpGraph graphAT
        let pphers = listOfNodes $ brokenUpGraph graphPT
        let aphers' = map (\x -> do {if isNothing x == True then 0.0 else (pherLevel (fromJust x))}) aphers
        let newPhers = zipWith (+) pphers aphers'
        graphFromEdges $ zip3 newPhers [1..] (adjVertsFromCombo graphPT)


